req_headers = {'User-Agent': 'Mozilla/5.0'}


def parse_site(url):
    r = requests.get(url, headers=req_headers).text

    soup = BeautifulSoup(r, 'html.parser')
    text = soup.find_all(text=True)

    x= {
        'title': soup.find_all('title')[0].text,
        'body': ''
    }

    for i in text:
        if i.parent.name == 'p':
            x['body'] += f'{i} '

    return x


for i in urls:
    print(parse_site(i))